import { useState, useEffect } from "react";

const StockManagement = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/products")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Erreur lors de la r√©cup√©ration des produits :", err));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/products/${id}`, { method: "DELETE" })
      .then(() => setProducts(products.filter((product) => product._id !== id)))
      .catch((err) => console.error("Erreur suppression :", err));
  };

  return (
    <div style={{ padding: "20px", textAlign: "center" }}>
      <h2>üì¶ G√©rer les Stocks</h2>
      <ul>
        {products.map((product) => (
          <li key={product._id} style={{ margin: "10px 0" }}>
            {product.name} - {product.price} DH
            <button onClick={() => handleDelete(product._id)} style={{ marginLeft: "10px", color: "red" }}>
              ‚ùå Supprimer
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StockManagement;
