import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "../styles/Home.css";

const Home = () => {
  const [products, setProducts] = useState([]);

  // üéØ R√©cup√©ration des produits
  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/products");
      if (!response.ok) throw new Error("Erreur lors du chargement des produits");

      const data = await response.json();
      console.log("Produits r√©cup√©r√©s:", data);
      setProducts(data);
    } catch (error) {
      console.error("‚ùå Erreur lors du fetch des produits:", error);
    }
  };

  // üîñ Gestion des favoris
  const toggleFavorite = async (id) => {
    try {
      const updatedProducts = products.map((product) =>
        product._id === id ? { ...product, isFavorite: !product.isFavorite } : product
      );
      setProducts(updatedProducts);

      // Envoyer au backend
      await fetch(`http://localhost:5000/api/products/${id}/favorite`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ isFavorite: !products.find(p => p._id === id).isFavorite })
      });
    } catch (error) {
      console.error("‚ùå Erreur mise √† jour favori:", error);
    }
  };

  // üîÑ Configuration du carrousel
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };

  return (
    <div>
      {/* üè† Navigation */}
      <header className="header">
        <nav className="navbar">
          <ul>
            <li><Link to="/">Accueil</Link></li>
            <li><Link to="/shop">Shop</Link></li>
            <li><Link to="/contact">Contact</Link></li>
          </ul>
        </nav>
        <div className="logo">
          <img src="https://cdn-icons-png.flaticon.com/128/892/892634.png" alt="Logo" />
        </div>
        <div className="right-icons">
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
            <img src="https://cdn-icons-png.flaticon.com/128/2111/2111463.png" alt="Instagram" />
          </a>
          <div className="whatsapp">
            <img src="https://cdn-icons-png.flaticon.com/128/3536/3536445.png" alt="WhatsApp" />
            <span>+212 6 XX XX XX XX</span>
          </div>
          <img src="https://cdn-icons-png.flaticon.com/128/3135/3135715.png" alt="Profil" className="profile-icon" />
        </div>
      </header>

      {/* üé† Carrousel */}
      <div className="carousel-container">
        <Slider {...settings}>
          <div><img src={`${process.env.PUBLIC_URL}/caroussel/moderne.jpeg`} alt="Moderne" /></div>
          <div><img src={`${process.env.PUBLIC_URL}/caroussel/style.jpeg`} alt="Style" /></div>
          <div><img src={`${process.env.PUBLIC_URL}/caroussel/traditionnel.jpeg`} alt="Traditionnel" /></div>
        </Slider>
      </div>

      {/* üõçÔ∏è Produits */}
      <div className="products-list">
        {products.length > 0 ? (
          products.map((product) => (
            <div key={product._id} className="product-card">
              <img src={`http://localhost:5000/uploads/${product.image}`} alt={product.name} />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p>Prix: {product.price}‚Ç¨</p>
              <button onClick={() => toggleFavorite(product._id)}>
                {product.isFavorite ? "üíñ Retirer des favoris" : "ü§ç Ajouter aux favoris"}
              </button>
            </div>
          ))
        ) : (
          <p>Aucun produit disponible</p>
        )}
      </div>

      {/* üñºÔ∏è Section des affiches */}
      <div className="image-grid">
        {[1, 2, 3, 4, 5, 6].map((num) => (
          <div className="image-box" key={num}>
            <img src={`${process.env.PUBLIC_URL}/affiche/affiche${num}.jpeg`} alt={`Affiche ${num}`} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
