import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "../styles/Home.css";

const Home = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    // 🔥 Récupérer les produits depuis l'API
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => {
        setProducts(data);
        setFilteredProducts(data); // Initialiser avec tous les produits
      })
      .catch((err) => console.error("Erreur lors du chargement des produits :", err));
  }, []);

  // 🔍 Fonction de recherche (nom ou marque)
  const handleSearch = (event) => {
    const term = event.target.value.toLowerCase();
    setSearchTerm(term);

    const filtered = products.filter(
      (product) =>
        product.name.toLowerCase().includes(term) ||
        product.brand.toLowerCase().includes(term) // Recherche par marque
    );

    setFilteredProducts(filtered);
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <div className="home-container">
      {/* 🔹 Header */}
      <header className="header">
        <nav className="navbar">
          {/* 🔹 Logo */}
          <div className="logo">
            <img src="https://cdn-icons-png.flaticon.com/128/892/892634.png" alt="Logo" />
          </div>

          {/* 🔹 Barre de recherche */}
          <input
            type="text"
            placeholder="Rechercher par nom ou marque..."
            value={searchTerm}
            onChange={handleSearch}
            className="search-bar"
          />

          {/* 🔹 Liens de navigation */}
          <ul className="nav-links">
            <li><Link to="/">Accueil</Link></li>
            <li><Link to="/shop">Shop</Link></li>
            <li><Link to="/contact">Contact</Link></li>
          </ul>

          {/* 🔹 Icônes à droite */}
          <div className="right-icons">
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
              <img src="https://cdn-icons-png.flaticon.com/128/2111/2111463.png" alt="Instagram" />
            </a>
            <div className="whatsapp">
              <img src="https://cdn-icons-png.flaticon.com/128/3536/3536445.png" alt="WhatsApp" />
              <span>+212 6 XX XX XX XX</span>
            </div>
            <img src="https://cdn-icons-png.flaticon.com/128/3135/3135715.png" alt="Profil" className="profile-icon" />
          </div>
        </nav>
      </header>

      {/* 🔹 Carrousel */}
      <div className="carousel-container">
        <Slider {...settings}>
          <div><img src={`${process.env.PUBLIC_URL}/caroussel/moderne.jpeg`} alt="Moderne" /></div>
          <div><img src={`${process.env.PUBLIC_URL}/caroussel/style.jpeg`} alt="Style" /></div>
          <div><img src={`${process.env.PUBLIC_URL}/caroussel/traditionnel.jpeg`} alt="Traditionnel" /></div>
        </Slider>
      </div>

      {/* 🔹 Section des 6 images */}
      <div className="image-grid">
        {[1, 2, 3, 4, 5, 6].map((num) => (
          <div className="image-box" key={num}>
            <img src={`${process.env.PUBLIC_URL}/affiche/affiche${num}.jpeg`} alt={`Affiche ${num}`} />
          </div>
        ))}
      </div>

      {/* 🔹 Résultats de la recherche */}
      <h2 className="section-title">🔍 Résultats de la recherche</h2>
      <div className="product-list">
        {filteredProducts.map((product) => (
          <div className="product-card" key={product._id}>
            <img src={`${process.env.PUBLIC_URL}/uploads/${product.image}`} alt={product.name} />
            <h3>{product.name}</h3>
            <p>Marque : {product.brand}</p>
            <p>Prix : {product.price} €</p>
          </div>
        ))}
      </div>

      {/* 🔹 Footer */}
      <footer className="footer">
        <p>© 2025 Tadlaoui Shop - Tous droits réservés.</p>
      </footer>
    </div>
  );
};

export default Home;
