import React, { useEffect, useState } from "react";
import "../styles/Home.css"; // Assurez-vous d'avoir un fichier CSS pour le style

const Home = () => {
  const [favoriteProducts, setFavoriteProducts] = useState([]);

  // üéØ Charger les produits favoris au montage du composant
  useEffect(() => {
    fetchFavorites();
  }, []);

  // üîÑ R√©cup√©ration des produits favoris depuis l'API
  const fetchFavorites = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/products"); // Ajustez l'URL selon votre backend
      if (!response.ok) throw new Error("Erreur lors du chargement des produits");

      const data = await response.json();

      // ‚úÖ Ne garder que les produits marqu√©s comme favoris
      const filteredFavorites = data.filter((product) => product.isFavorite);
      setFavoriteProducts(filteredFavorites);
    } catch (error) {
      console.error("‚ùå Erreur lors du fetch des favoris:", error);
    }
  };

  return (
    <div className="home-container">
      <h2 className="home-title">üñºÔ∏è Tableaux Favoris</h2>

      {favoriteProducts.length === 0 ? (
        <p className="no-favorites">Aucun tableau favori pour le moment.</p>
      ) : (
        <div className="favorites-grid">
          {favoriteProducts.map((product) => (
            <div key={product._id} className="favorite-item">
              <img
                src={`http://localhost:5000/uploads/${product.image}`} // Assurez-vous que les images sont bien accessibles
                alt={product.name}
                className="favorite-image"
              />
              <h3 className="favorite-name">{product.name}</h3>
              <p className="favorite-price">{product.price} DH</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
