import { useState } from "react";

const AdminDashboard = () => {
  const [product, setProduct] = useState({
    name: "",
    price: "",
    description: "",
    image: "",
  });

  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("üîç Produit envoy√© :", product); // V√©rifie si √ßa s'affiche

    try {
      const response = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(product),
      });

      const data = await response.json();
      console.log("‚úÖ R√©ponse serveur :", data);

      if (response.ok) {
        alert("Produit ajout√© !");
        setProduct({ name: "", price: "", description: "", image: "" });
      } else {
        alert("Erreur : " + data.message);
      }
    } catch (error) {
      console.error("‚ùå Erreur lors de l'ajout :", error);
    }
  };

  return (
    <div>
      <h2>Dashboard Admin</h2>
      <button onClick={() => localStorage.removeItem("adminAuth")}>Se d√©connecter</button>

      <div>
        <h3>Ajout de produit</h3>
        <form onSubmit={handleSubmit}>
          <input type="text" name="name" placeholder="Nom" value={product.name} onChange={handleChange} required />
          <input type="number" name="price" placeholder="Prix" value={product.price} onChange={handleChange} required />
          <input type="text" name="description" placeholder="Description" value={product.description} onChange={handleChange} required />
          <input type="text" name="image" placeholder="URL de l'image" value={product.image} onChange={handleChange} required />
          <button type="submit">Ajouter</button>
        </form>
      </div>
    </div>
  );
};

export default AdminDashboard;
