import { useEffect, useState } from "react";

const DeleteProduct = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then(res => res.json())
      .then(data => setProducts(data))
      .catch(err => console.error("Erreur :", err));
  }, []);

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm("Es-tu sûr de vouloir supprimer ce produit ?");
    if (!confirmDelete) return;
  
    try {
      const res = await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
      });
  
      if (res.ok) {
        setProducts(products.filter(product => product._id !== id));
      } else {
        const errorData = await res.json();
        console.error("Erreur lors de la suppression :", errorData);
      }
    } catch (err) {
      console.error("Erreur serveur :", err);
    }
  };
  

  return (
    <div>
      <h2>❌ Supprimer un produit</h2>
      {products.length === 0 ? <p>Aucun produit disponible.</p> : (
        <ul>
          {products.map((product) => (
            <li key={product._id}>
              <img src={`http://localhost:5000${product.image}`} alt={product.name} style={{ width: "80px", height: "80px", objectFit: "cover" }} />
              {product.name} - {product.price}€
              <button onClick={() => handleDelete(product._id)}>Supprimer</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default DeleteProduct;
