// Register.js
import React, { useState } from 'react';
import { signUpWithEmailPassword, signInWithGoogle } from '../firebase';
import '../styles/Auth.css';  // Assure-toi que ce fichier existe et contient le style approprié

const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      await signUpWithEmailPassword(email, password);
      setError('');
      alert('Inscription réussie !');
    } catch (err) {
      setError('Erreur lors de l\'inscription : ' + err.message);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      await signInWithGoogle();
      alert('Connexion réussie avec Google !');
    } catch (err) {
      setError('Erreur de connexion avec Google : ' + err.message);
    }
  };

  return (
    <div className="auth-container">
      <h2>Inscription</h2>
      <form onSubmit={handleRegister} className="auth-form">
        <div className="input-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Entrez votre email"
            required
          />
        </div>
        <div className="input-group">
          <label>Mot de passe</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Entrez votre mot de passe"
            required
          />
        </div>
        {error && <div className="error-message">{error}</div>}
        <button type="submit" className="submit-btn">S'inscrire</button>
      </form>

      <div className="google-login">
        <p>Ou inscrivez-vous avec Google</p>
        <button onClick={handleGoogleLogin} className="google-btn">
          Connexion avec Google
        </button>
      </div>
    </div>
  );
};

export default Register;
