import React, { useState } from "react";
import emailjs from "@emailjs/browser";

const Contact = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    emailjs
      .send(
        "SERVICE_ID", // Remplace avec ton Service ID
        "TEMPLATE_ID", // Remplace avec ton Template ID
        {
          from_name: form.name,
          from_email: form.email,
          message: form.message,
        },
        "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" // Remplace avec ta clé publique EmailJS
      )
      .then(
        () => {
          alert("Message envoyé avec succès !");
          setForm({ name: "", email: "", message: "" });
        },
        (error) => {
          console.error("Erreur lors de l'envoi:", error);
          alert("Erreur lors de l'envoi du message.");
        }
      );
  };

  return (
    <div className="contact-container">
      <h2>Contactez-nous</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Votre nom"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Votre email"
          value={form.email}
          onChange={handleChange}
          required
        />
        <textarea
          name="message"
          placeholder="Votre message"
          value={form.message}
          onChange={handleChange}
          required
        />
        <button type="submit">Envoyer</button>
      </form>
    </div>
  );
};

export default Contact;
