import React, { useState } from 'react';
import { signInWithGoogle, signInWithEmailPassword } from '../firebase';
import '../styles/Auth.css';  // Assurez-vous que votre fichier CSS est bien import√©

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailPassword(email, password);
      setError('');
      alert('Connexion r√©ussie!');
    } catch (err) {
      setError('Erreur lors de la connexion: ' + err.message);
    }
  };

  const handleGoogleLogin = async () => {
    try {
      await signInWithGoogle();
      alert('Connexion r√©ussie avec Google!');
    } catch (err) {
      setError('Erreur de connexion avec Google: ' + err.message);
    }
  };

  return (
    <div className="auth-container">
      <h2>Connexion</h2>
      <form onSubmit={handleLogin} className="auth-form">
        <div className="input-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Entrez votre email"
            required
          />
        </div>
        <div className="input-group">
          <label>Mot de passe</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Entrez votre mot de passe"
            required
          />
        </div>
        {error && <div className="error-message">{error}</div>}
        <button type="submit" className="submit-btn">Se connecter</button>
      </form>

      <div className="google-login">
        <p>Ou connectez-vous avec</p>
        <button onClick={handleGoogleLogin} className="google-btn">
          <span role="img" aria-label="google">üåç</span> Connexion avec Google
        </button>
      </div>
    </div>
  );
};

export default Login;
