import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import '../styles/Home.css';

const Home = () => {
  const [favoriteProducts, setFavoriteProducts] = useState([]);

  // 🎯 Charger les produits favoris au montage du composant
  useEffect(() => {
    fetchFavorites();
  }, []);

  // 🔄 Récupération des produits favoris depuis l'API
  const fetchFavorites = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/products"); // Assurez-vous que l'URL est correcte
      if (!response.ok) throw new Error("Erreur lors du chargement des produits");

      const data = await response.json();

      // ✅ Ne garder que les produits marqués comme favoris
      const filteredFavorites = data.filter((product) => product.isFavorite);
      setFavoriteProducts(filteredFavorites);
    } catch (error) {
      console.error("❌ Erreur lors du fetch des favoris:", error);
    }
  };

  // 🎠 Configuration du carrousel
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };

  return (
    <div>
      {/* 🏠 Header avec Navbar */}
      <header className="header">
        <nav className="navbar">
          <ul>
            <li><Link to="/">Accueil</Link></li>
            <li><Link to="/shop">Shop</Link></li>
            <li><Link to="/contact">Contact</Link></li>
          </ul>
        </nav>
        <div className="logo">
          <img src="https://cdn-icons-png.flaticon.com/128/892/892634.png" alt="Logo" />
        </div>
        <div className="right-icons">
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
            <img src="https://cdn-icons-png.flaticon.com/128/2111/2111463.png" alt="Instagram" />
          </a>
          <div className="whatsapp">
            <img src="https://cdn-icons-png.flaticon.com/128/3536/3536445.png" alt="WhatsApp" />
            <span>+212 6 XX XX XX XX</span>
          </div>
          <img src="https://cdn-icons-png.flaticon.com/128/3135/3135715.png" alt="Profil" className="profile-icon" />
        </div>
      </header>

      {/* 🎠 Carrousel */}
      <div className="carousel-container">
        <Slider {...settings}>
          <div>
            <img src={`${process.env.PUBLIC_URL}/caroussel/moderne.jpeg`} alt="Slide 1" />
          </div>
          <div>
            <img src={`${process.env.PUBLIC_URL}/caroussel/style.jpeg`} alt="Slide 2" />
          </div>
          <div>
            <img src={`${process.env.PUBLIC_URL}/caroussel/traditionnel.jpeg`} alt="Slide 3" />
          </div>
        </Slider>
      </div>

      {/* 🖼️ Section Tableaux Favoris */}
      <div className="home-container">
        <h2 className="home-title">🖼️ Tableaux Favoris</h2>

        {favoriteProducts.length === 0 ? (
          <p className="no-favorites">Aucun tableau favori pour le moment.</p>
        ) : (
          <div className="favorites-grid">
            {favoriteProducts.map((product) => (
              <div key={product._id} className="favorite-item">
                <img
                  src={`http://localhost:5000/uploads/${product.image}`} // Assurez-vous que le backend sert bien les images
                  alt={product.name}
                  className="favorite-image"
                />
                <h3 className="favorite-name">{product.name}</h3>
                <p className="favorite-price">{product.price} DH</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
