import { useState, useEffect } from "react";

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    price: "",
    description: "",
    image: null, // Stocke l'image sous forme de fichier
  });

  // Charger les produits au démarrage
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/products");
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error("❌ Erreur lors du chargement des produits :", error);
      }
    };

    fetchProducts();
  }, []);

  // Gestion des inputs du formulaire
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image") {
      setFormData({ ...formData, image: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  // Soumettre le formulaire d'ajout de produit
  const handleSubmit = async (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    formDataToSend.append("name", formData.name);
    formDataToSend.append("price", formData.price);
    formDataToSend.append("description", formData.description);
    formDataToSend.append("image", formData.image);

    try {
      const response = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        body: formDataToSend,
      });

      if (!response.ok) throw new Error("Erreur lors de l'ajout du produit");

      const newProduct = await response.json();
      setProducts([...products, newProduct]); // Mettre à jour la liste des produits
      setFormData({ name: "", price: "", description: "", image: null }); // Réinitialiser le formulaire
    } catch (error) {
      console.error("❌ Erreur :", error);
    }
  };

  return (
    <div>
      <h2>Dashboard Admin</h2>
      <button onClick={() => localStorage.removeItem("adminAuth")}>Se déconnecter</button>

      {/* Formulaire d'ajout de produit */}
      <div>
        <h3>Ajout de produit</h3>
        <form onSubmit={handleSubmit}>
          <input type="text" name="name" placeholder="Nom du produit" value={formData.name} onChange={handleChange} required />
          <input type="number" name="price" placeholder="Prix" value={formData.price} onChange={handleChange} required />
          <textarea name="description" placeholder="Description" value={formData.description} onChange={handleChange} required></textarea>
          <input type="file" name="image" accept="image/*" onChange={handleChange} required />
          <button type="submit">Ajouter</button>
        </form>
      </div>

      {/* Liste des produits */}
      <div>
        <h3>Liste des produits</h3>
        <ul>
          {products.length > 0 ? (
            products.map((product) => (
              <li key={product._id}>
                <h4>{product.name}</h4>
                <p>Prix : {product.price}€</p>
                <p>{product.description}</p>
                <img src={`http://localhost:5000/uploads/${product.image}`} alt={product.name} width="100" />

              </li>
            ))
          ) : (
            <p>Aucun produit disponible.</p>
          )}
        </ul>
      </div>
    </div>
  );
};

export default AdminDashboard;
