import { useState } from "react";

const AdminDashboard = () => {
  const [product, setProduct] = useState({
    name: "",
    price: "",
    description: "",
    image: null,
  });

  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setProduct({ ...product, image: e.target.files[0] }); // Récupère l'image sélectionnée
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const formData = new FormData();
    formData.append("name", product.name);
    formData.append("price", product.price);
    formData.append("description", product.description);
    formData.append("image", product.image);

    try {
      const response = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        body: formData, // Envoi de FormData (pas besoin de headers)
      });

      const data = await response.json();
      console.log("✅ Réponse serveur :", data);

      if (response.ok) {
        alert("Produit ajouté !");
        setProduct({ name: "", price: "", description: "", image: null });
      } else {
        alert("Erreur : " + data.message);
      }
    } catch (error) {
      console.error("❌ Erreur lors de l'ajout :", error);
    }
  };

  return (
    <div>
      <h2>Dashboard Admin</h2>
      <button onClick={() => localStorage.removeItem("adminAuth")}>Se déconnecter</button>

      <div>
        <h3>Ajout de produit</h3>
        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <input type="text" name="name" placeholder="Nom" value={product.name} onChange={handleChange} required />
          <input type="number" name="price" placeholder="Prix" value={product.price} onChange={handleChange} required />
          <input type="text" name="description" placeholder="Description" value={product.description} onChange={handleChange} required />
          <input type="file" name="image" accept="image/*" onChange={handleFileChange} required />
          <button type="submit">Ajouter</button>
        </form>
      </div>
    </div>
  );
};

export default AdminDashboard;
