// Login.js
import React, { useState } from "react";
import { signInWithGoogle, signOutUser, signUpWithEmailPassword, signInWithEmailPassword } from "../firebase";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isSignUp, setIsSignUp] = useState(false);

  const handleGoogleLogin = async () => {
    const user = await signInWithGoogle();
    if (user) {
      console.log("Utilisateur connecté avec Google", user);
    }
  };

  const handleEmailPasswordSubmit = async (event) => {
    event.preventDefault();
    if (isSignUp) {
      const user = await signUpWithEmailPassword(email, password);
      if (user) {
        console.log("Utilisateur inscrit avec Email et Mot de passe", user);
      }
    } else {
      const user = await signInWithEmailPassword(email, password);
      if (user) {
        console.log("Utilisateur connecté avec Email et Mot de passe", user);
      }
    }
  };

  return (
    <div>
      <h2>{isSignUp ? "Inscription" : "Connexion"}</h2>
      <form onSubmit={handleEmailPasswordSubmit}>
        <div>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Mot de passe</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">{isSignUp ? "S'inscrire" : "Se connecter"}</button>
      </form>
      <button onClick={() => setIsSignUp(!isSignUp)}>
        {isSignUp ? "Déjà un compte ? Se connecter" : "Pas de compte ? S'inscrire"}
      </button>
      <button onClick={handleGoogleLogin}>Se connecter avec Google</button>
      <button onClick={signOutUser}>Se déconnecter</button>
    </div>
  );
};

export default Login; // N'oublie pas d'exporter le composant
