import { useState } from "react";

const AddProduct = () => {
  const [product, setProduct] = useState({ name: "", price: "", description: "", image: "" });

  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("http://localhost:5000/products", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(product),
    })
      .then((res) => res.json())
      .then(() => alert("Produit ajouté !"))
      .catch((err) => console.error("Erreur ajout :", err));
  };

  return (
    <div style={{ padding: "20px", textAlign: "center" }}>
      <h2>➕ Ajouter un produit</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Nom du produit" onChange={handleChange} required />
        <input type="number" name="price" placeholder="Prix" onChange={handleChange} required />
        <textarea name="description" placeholder="Description" onChange={handleChange} required />
        <input type="text" name="image" placeholder="Image URL" onChange={handleChange} required />
        <button type="submit">Ajouter</button>
      </form>
    </div>
  );
};

export default AddProduct;
