import { useState, useEffect } from "react";

const StockManagement = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/products")
      .then((res) => res.json())
      .then((data) => {
        console.log("üì¶ Produits r√©cup√©r√©s :", data); // Debug
        setProducts(data);
      })
      .catch((err) => console.error("‚ùå Erreur r√©cup√©ration des produits :", err));
  }, []);

  return (
    <div style={{ padding: "20px", textAlign: "center" }}>
      <h2>üì¶ G√©rer les Stocks</h2>
      {products.length === 0 ? (
        <p>Aucun produit disponible.</p>
      ) : (
        <ul>
          {products.map((product) => (
            <li key={product._id} style={{ margin: "10px 0" }}>
              <img src={`http://localhost:5000/uploads/${product.image}`} alt={product.name} style={{ width: "100px" }} />
              {product.name} - {product.price} DH
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default StockManagement;
